import { EvalFunction } from "@/types/evals";
import { z } from "zod";

export const upload_file: EvalFunction = async ({
  logger,
  debugUrl,
  sessionUrl,
  stagehand,
  useTextExtract,
}) => {
  try {
    await stagehand.page.goto("https://browser-tests-alpha.vercel.app/api/upload-test");

    const observations = await stagehand.page.observe("Upload a file to the file input with id 'fileUpload'");
    if (observations.length !== 1) {
      await stagehand.close();
      return {
        _success: false,
        observations,
        debugUrl,
        sessionUrl,
        logs: logger.getLogs(),
      };
    }

    const uploadObservation = observations[0]!;
    uploadObservation.arguments = [{
      name: "emoji.png",
      mimeType: "image/png",
      buffer: Buffer.from("data:image/png;base64,"),
    }];

    await stagehand.page.act(uploadObservation);

    const fileSize = await stagehand.page.extract({
      instruction: "Get the size of the file. It should be the number inside the span with id fileSize.",
      schema: z.object({
        fileSize: z.number().describe("The size of the file in bytes"),
      }),
      useTextExtract,
    })

    if (!fileSize || !fileSize.fileSize) {
      await stagehand.close();
      return {
        _success: false,
        logs: logger.getLogs(),
        debugUrl,
        sessionUrl,
      };
    }

    await stagehand.close();

    return {
      _success: true,
      fileSize,
      logs: logger.getLogs(),
      debugUrl,
      sessionUrl,
    };
  } catch (error) {
    logger.error({
      message: `error in upload_file function`,
      level: 0,
      auxiliary: {
        error: {
          value: error.message,
          type: "string",
        },
        trace: {
          value: error.stack,
          type: "string",
        },
      },
    });

    await stagehand.close();

    return {
      _success: false,
      logs: logger.getLogs(),
      debugUrl,
      sessionUrl,
    };
  }
};
