scripts.build:esm:
pnpm -w exec tsc -p packages/evals/tsconfig.esm.json && node -e "const fs=require('node:fs');const path=require('node:path');const out=path.join('dist','esm');fs.mkdirSync(out,{recursive:true});fs.writeFileSync(path.join(out,'package.json'), '{
  "type": "module"
}
');for (const file of ['evals.config.json']) {const src=path.join(file);const dest=path.join(out,file);if (fs.existsSync(src)) {fs.copyFileSync(src,dest);}}for (const dir of ['datasets','assets']) {const srcDir=path.join(dir);const destDir=path.join(out,dir);if (fs.existsSync(srcDir)) {fs.cpSync(srcDir,destDir,{recursive:true});}}"
